package com.emarte.regurgitator.test;import com.emarte.regurgitator.core.CreateResponseYmlLoader;import com.emarte.regurgitator.core.RegurgitatorException;import org.junit.Test;import static com.emarte.regurgitator.core.ConfigurationFile.loadFile;public class CreateResponseYmlLoaderTest extends YmlLoaderTest {    public CreateResponseYmlLoaderTest() {        super(new CreateResponseYmlLoader());    }    @Test    public void testValueYml() throws Exception {        assertExpectation("classpath:/CreateResponse_value.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[null,'value'],null]");    }    @Test    public void testFileYml() throws Exception {        assertExpectation("classpath:/CreateResponse_file.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[null,'file-value'],null]");    }    @Test    public void testSourceYml() throws Exception {        assertExpectation("classpath:/CreateResponse_source.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[com.emarte.regurgitator.core.ContextLocation:['context:location'],null],null]");    }    @Test    public void testSourceAndValueYml() throws Exception {        assertExpectation("classpath:/CreateResponse_sourceAndValue.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[com.emarte.regurgitator.core.ContextLocation:['context:location'],'value'],null]");    }    @Test    public void testSourceAndFileYml() throws Exception {        assertExpectation("classpath:/CreateResponse_sourceAndFile.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[com.emarte.regurgitator.core.ContextLocation:['context:location'],'file-value'],null]");    }    @Test    public void testProcessorYml() throws Exception {        assertExpectation("classpath:/CreateResponse_processor.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[null,'value'],com.emarte.regurgitator.test.stuff.TestValueProcessor:[]]");    }    @Test    public void testProcessorConfigYml() throws Exception {        assertExpectation("classpath:/CreateResponse_processorConfig.yml", "com.emarte.regurgitator.core.CreateResponse:['create-response-1',com.emarte.regurgitator.core.ValueSource:[null,'value'],com.emarte.regurgitator.test.stuff.TestValueProcessor:[]]");    }    @Test    public void testFullLoadYml() throws Exception {        loadFile("classpath:/CreateResponse_fullLoad.yml");    }    @Test(expected = RegurgitatorException.class)    public void testValueAndFileExceptionYml() throws Exception {        loadFromFile("classpath:/CreateResponse_valueAndFile.yml");    }    @Test(expected = RegurgitatorException.class)    public void testNothingExceptionYml() throws Exception {        loadFromFile("classpath:/CreateResponse_nothing.yml");    }    @Test(expected = RegurgitatorException.class)    public void testMissingFileExceptionYml() throws Exception {        loadFromFile("classpath:/CreateResponse_missingFile.yml");    }}