package com.emarte.regurgitator.test;import com.emarte.regurgitator.core.IdentifySessionYmlLoader;import com.emarte.regurgitator.core.RegurgitatorException;import org.junit.Test;import static com.emarte.regurgitator.core.ConfigurationFile.loadFile;public class IdentifySessionYmlLoaderTest extends YmlLoaderTest {	public IdentifySessionYmlLoaderTest() {		super(new IdentifySessionYmlLoader());	}	@Test	public void testSourceYml() throws Exception {		assertExpectation("classpath:/IdentifySession_source.yml", "com.emarte.regurgitator.core.IdentifySession:['identify-session-1',com.emarte.regurgitator.core.ValueSource:[com.emarte.regurgitator.core.ContextLocation:['context:location'],null]]");	}	@Test	public void testValueYml() throws Exception {		assertExpectation("classpath:/IdentifySession_value.yml", "com.emarte.regurgitator.core.IdentifySession:['identify-session-1',com.emarte.regurgitator.core.ValueSource:[null,'value']]");	}	@Test	public void testSourceAndValueYml() throws Exception {		assertExpectation("classpath:/IdentifySession_sourceAndValue.yml", "com.emarte.regurgitator.core.IdentifySession:['identify-session-1',com.emarte.regurgitator.core.ValueSource:[com.emarte.regurgitator.core.ContextLocation:['context:location'],'value']]");	}	@Test	public void testFullLoadYml() throws Exception {		loadFile("classpath:/IdentifySession_fullLoad.yml");	}	@Test(expected = RegurgitatorException.class)	public void testNothingExceptionYml() throws Exception {		loadFromFile("classpath:/IdentifySession_nothing.yml");	}}